package com.qsj.qsjMain.config;import lombok.extern.slf4j.Slf4j;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import java.net.InetAddress;/** * hostname and address config */@Configuration@Slf4jpublic class EnvConf {    public static String hostname = "";    public static String address = "";    public static String coordinateNodeId = "0";    @Bean    public String initEnv() {        try {            hostname = InetAddress.getLocalHost().getHostName();            address = InetAddress.getLocalHost().getHostAddress();            String nodeId = System.getProperty("COORDINATE_NODE_ID", "0");            log.info("initializing with COORDINATE_NODE_ID: {}", nodeId);            coordinateNodeId = nodeId;        } catch (Exception e) {            hostname = "";            address = "";        }        return hostname + "/" + address;    }}