package com.qsj.qsjMain.config;import lombok.Data;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Configuration;/** * profile variable config */@Data@Configurationpublic class ProfileVariableConfig {    @Value("${qsj.url.delivery-callback}")    private String deliveryCallbackURL;    @Value("${qsj.url.pay-callback}")    private String payCallbackURL;    @Value("${qsj.qrcode.type}")    private String qrcodeType;    @Value("${spring.profiles.active}")    private String activeProfile;    @Value("${qsj.delivery.sourceId}")    private String deliverySourceId;    @Value("${qsj.url.delivery-prefix}")    private String dadaDeliveryPrefix;    @Value("${qsj.url.judanke-prefix}")    private String juDanKeDeliveryPrefix;    @Value("${qsj.ewx.sToken}")    private String ewxAppToken;    @Value("${qsj.ewx.sCorpID}")    private String ewxCorpId;    @Value("${qsj.ewx.sEncodingAESKey}")    private String encodingAESKey;    public boolean isProd() {        return "prod".equals(activeProfile);    }}